package int转换为char类型;

public class IntChar {
	//1111+222
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=(int)'Z';
		System.out.println(a);
		//凌帅
		char b='A';
		int c=b;
		System.out.println(b+":"+c);
		int n=3;
		char ac=(char)(n+48);
		System.out.println(ac);
	}

}
